import React, { useEffect, useState } from 'react'
import Dropdown from '../components/Buttons/Dropdown'
import NoData from '../components/Templates/NoData'
import Page from '../components/Templates/Page'
import { fetchGateways, fetchGrouped, fetchProjects, fetchReport } from '../utils/api'

export default function Home() {
  const [displayNoData, setDisplayNoData] = useState(true)
  const [displayProjects, setDisplayProjects] = useState(false)
  const [displayGateways, setDisplayGateways] = useState(false)

  const [projectsList, setProjectsList] = useState([])

  const [projectId, setProjectId] = useState(null)
  const [gatewayId, setGatewayId] = useState(null)
  const [fromDate, setFromDate]   = useState('1900-01-01')
  const [toDate, setToDate]       = useState('2022-06-16')

  const [projectName, setProjectName] = useState('Select project')
  const [gatewayName, setGatewayName] = useState('Select gateway')

  const [projectsBnt, setProjectsButton]  = useState(null)
  const [gatewaysBtn, setGatewaysBtn]     = useState(null)

  const [projectsAccordion, setProjectsAccordion] = useState([])

  const [projectsTotal, setProjectsTotal] = useState(0)

  const getProjects = async () => {
    try {
      let response = await fetchProjects()
      setProjectsButton(response.data.map((project) => 
          <option value={project.projectId}>{project.name}</option>
        ))
    } catch (err) {
      console.log(err)
    }
  }

  const getGetaways = async () => {
    try {
      let response = await fetchGateways()
      setGatewaysBtn(response.data.map((gateway) => 
        <option value={gateway.gatewayId}>{gateway.name}</option>
      ))
    } catch (err) {
      console.log(err)
    }
  }

  const getAll = async (fromDate, toDate, projectId, gatewayId) => {
    try {
      let response = await fetchGrouped(fromDate, toDate, projectId, gatewayId)
      setProjectsList(response.projects) 
    } catch (err) {
      console.log(err)
    }
  }

  useEffect(()=> {
    getProjects()
    getGetaways()
  }, [])

  const displayProject = async () => {
    await getAll(fromDate, toDate, projectId, gatewayId)
    setProjectsAccordion([])
    let totalAmount = 0
    console.log(projectsList)
    projectsList.forEach(element => {
      totalAmount += Number(element.totalAmount)
    });
    setProjectsTotal(totalAmount.toFixed(2))
    projectsList.forEach(project => {
      setProjectsAccordion(projectsAccordion => [...projectsAccordion,
        <div className="accordion-item mt-2 rounded-10 border-0" key={project.projectId}>
          <h2 className="accordion-header" id={`flush-headint${project.projectId}`}>
            <button className="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target={`#flush-collapse${project.projectId}`} aria-expanded="false" aria-controls={`flush-collapse${project.projectId}`}>
              <div className="d-flex justify-content-between fw-700 text-blue-900 p-2 w-100">
                <span>
                  { project.name }
                </span>
                <span>
                  TOTAL: {project.totalAmount} USD
                </span>
              </div>
            </button>
          </h2>
          <div id={`flush-collapse${project.projectId}`} className="accordion-collapse collapse bg-lightblue" aria-labelledby={`flush-headint${project.projectId}`} data-bs-parent="#accordionFlushExample">
            <div className="accordion-body">
              <table className="table table-hover table-sm">
                <thead className="thead-light">
                  <tr key="head">
                    <th scope="col">Date</th>
                    <th scope="col">Gateaway</th>
                    <th scope="col">Transaction ID</th>
                    <th scope="col">Amount</th>
                  </tr>
                </thead>
                <tbody>
                  { project.transactions.map((transaction) => 
                    <tr key={transaction.paymentId}>
                      <th>{transaction.created}</th>
                      <th>{transaction.gatewayName}</th>
                      <th>{transaction.paymentId}</th>
                      <th>{transaction.amount}</th>
                    </tr>
                  )}
                </tbody>

              </table>
            </div>
          </div>
        </div>
      ])
    });
    setDisplayNoData(false)
    setDisplayProjects(true)
  }

  const selectProject = (e) => {
    console.log(e.target.value)
    setProjectId(e.target.value)
  }

  const selectGateway = (e) => {
    console.log(e.target.value)
    setGatewayId(e.target.value)
  }

  const showReport = async () => {
    await getAll(fromDate, toDate, projectId, gatewayId)
    console.log(projectsList)

  }

  return (
    <>
        <Page> 
          <div className="d-flex justify-content-between">
            <div className="container">
              <h4 className="p-0 m-0 fs-bolder text-blue-900">
                Reports
              </h4>
              <span className="text-gray p-0 m-0 fw-700">
                Easily generate a report of your transactions
              </span>
            </div>
            <div className="d-flex justify-content-end">

              {/* Projects Dropdown */}
              <select 
                className="btn btn-green text-white dropdown-toggle form-sm me-4 fs-sm" 
                aria-label="Select project"
                value={projectId}
                onChange={selectProject}
              >
                <option selected>Select project</option>
                <option value="">All projects</option>
                { projectsBnt }
              </select>

              {/* Gateways Dropdown */}
              <select 
                className="btn btn-green text-white dropdown-toggle form-sm me-4 fs-sm" 
                aria-label="Select project"
                value={gatewayId}
                onChange={selectGateway}
              >
                <option selected>Select gateway</option>
                <option value="">All gateways</option>
                { gatewaysBtn }
              </select>

              {/* Generate report button */}
              <button
                className="btn btn-sm btn-blue text-white btn-long form-sm fs-sm" 
                onClick={showReport}
              >
                Generate report
              </button>
          </div>
          </div>

          <NoData 
            display={displayNoData}
          />

          <div className="d-flex justify-content-even">
          <div className="container">
            <div className={`${displayProjects ? 'd-block' : 'd-none'} rounded-more p-3 bg-lightblue mt-3 mb-3 me-2 ${displayGateways ? 'width-50' : 'width-100'}`} >
              <span className="text-blue-900 fw-700">
                { projectName } | { gatewayName }
              </span>
              <div className="accordion accordion-flush" id="accordionFlushExample">
                { projectsAccordion }
              </div>
            </div>
            <div className={`${displayProjects ? 'd-block' : 'd-none'} text-blue-900 fw-700 rounded-more p-3 bg-lightblue mt-3 mb-3 me-2 ${displayGateways ? 'width-50' : 'width-100'}`} >
              TOTAL: {projectsTotal} USD
            </div>
          </div>

          <div className={`${displayGateways ? 'd-block' : 'd-none'} mt-3 mb-3 ${displayGateways ? 'width-50' : 'width-100'}`} >
            <span className={`text-blue-900 fw-700 rounded-more p-3 bg-lightblue ${displayGateways ? 'width-50' : 'width-100'}`}>
              chart
            </span>
          </div>
          </div>
        </Page>
    </>
  )
}
