import React, { useEffect, useState } from 'react'
import NoData from '../components/Templates/NoData'
import Page from '../components/Templates/Page'
import { fetchGateways, fetchProjects, fetchReport } from '../utils/api'

export default function Home() {
  const [displayNoData, setDisplayNoData] = useState(true)
  const [displayProjects, setDisplayProjects] = useState('d-none')
  const [displayGateways, setDisplayGateways] = useState('d-none')

  const [reportList, setReportList] = useState([])
  const [projectsList, setProjectsList] = useState([])

  const [projectName, setProjectName] = useState('Select project')
  const [gatewayName, setGatewayName] = useState('Select gateway')

  const [projectsBnt, setProjectsButton] = useState(null)
  const [gatewaysBtn, setGatewaysBtn] = useState(null)

  const [projectsAccordion, setProjectsAccordion] = useState([])

  const getProjects = async () => {
    try {
      let response = await fetchProjects()
      setProjectsButton(response.data.map((project) => <li key={project.projectId}><button className="dropdown-item"> {project.name} </button></li>))
      setProjectsList(response.data)
    } catch ( err ) {
      console.log(err)
    }
  } 

  const getGateways = async () => {
    try {
      let response = await fetchGateways()
      setGatewaysBtn(response.data.map((gateway) => <li key={gateway.gatewayId}><button className="dropdown-item"> {gateway.name} </button></li>))
    } catch ( err ) {
      console.log(err)
    }
  }

  const getReport = async (data) => {
    try {
      let response = await fetchReport(data)
      setReportList(response.data)
    } catch ( err ) {
      console.log(err)
    }
  }

  useEffect(()=> {
    getReport({from:'2020-01-01', to:'2022-06-06'})
  }, [])

  useEffect(()=> {
    getProjects()
    getGateways()
  }, [reportList])

  const displayProject = () => {

    projectsList.forEach(project => {
      setProjectsAccordion(projectsAccordion => [...projectsAccordion,
        <div className="accordion-item" key={project.projectId}>
          <h2 className="accordion-header" id={`flush-headint${project.projectId}`}>
            <button className="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target={`#flush-collapse${project.projectId}`} aria-expanded="false" aria-controls={`flush-collapse${project.projectId}`}>
              <span className="text-white">
                { project.name }
              </span>
            </button>
          </h2>
          <div id={`flush-collapse${project.projectId}`} className="accordion-collapse collapse" aria-labelledby={`flush-headint${project.projectId}`} data-bs-parent="#accordionFlushExample">
            <div className="accordion-body">Placeholder content for this accordion, which is intended to demonstrate the <code>.accordion-flush</code> class. This is the first item's accordion body.</div>
          </div>
        </div>
      ])
    });


    setProjectName('All projects')
    setGatewayName('All gateways')
    setDisplayNoData(false)
    setDisplayProjects('d-block')
  }

  return (
    <>
        <Page> 
          <div className="d-flex justify-content-between">
            <div className="container">
              <h4 className="p-0 m-0 fs-bolder text-blue-900">
                Reports
              </h4>
              <span className="text-gray p-0 m-0 fw-700">
                Easily generate a report of your transactions
              </span>
            </div>
            <div className="d-flex justify-content-end">
              {/* Projects Dropdown */}
              <div className="dropdown me-4">
                  <button className="btn btn-sm btn-green text-white dropdown-toggle" type="button" id="projects" data-bs-toggle="dropdown" aria-expanded="false">
                    { projectName }
                  </button>
                  <ul className="dropdown-menu" aria-labelledby="projects">
                    <li><button onClick={displayProject} className="dropdown-item"> All projects </button></li>
                      { projectsBnt }
                  </ul>
              </div>

              {/* Gateways Dropdown */}
              <div className="dropdown me-4">
                  <button className="btn btn-sm btn-green text-white dropdown-toggle" type="button" id="gateways" data-bs-toggle="dropdown" aria-expanded="false">
                    { gatewayName }
                  </button>
                  <ul className="dropdown-menu" aria-labelledby="gateways">
                    <li><button onClick={displayProject} className="dropdown-item"> All gateways </button></li>
                      { gatewaysBtn }
                  </ul>
              </div>
          </div>
          </div>

          <NoData 
            display={displayNoData}
          />

          <div className="d-flex justify-content-even">
          <div className={`${displayProjects} rounded-more p-3 bg-lightblue mt-3 mb-3 me-2 ${displayGateways ? 'width-100' : 'width-50'}`} >
            <span className="text-blue-900 fw-700">
              { projectName } | { gatewayName }
            </span>
            <div className="accordion accordion-flush" id="accordionFlushExample">
              { projectsAccordion }
            </div>
          </div>

          <div className={`${displayGateways} rounded-more p-3 bg-lightblue mt-3 mb-3 ${displayGateways ? 'width-100' : 'width-50'}`} >
            <span className="text-blue-900 fw-700">
              { projectName } | { gatewayName }
            </span>
          </div>

          </div>

        </Page>
    </>
  )
}
