import React, { useEffect, useState } from 'react'
import NoData from '../components/Templates/NoData'
import Page from '../components/Templates/Page'
import { fetchGateways, fetchProjects } from '../utils/api'

export default function Home() {
  const [displayNoData, setDisplayNoData] = useState(true)
  const [displayProjects, setDisplayProjects] = useState('d-none')
  const [displayGateways, setDisplayGateways] = useState('d-none')

  const [projectName, setProjectName] = useState('Select project')
  const [gatewayName, setGatewayName] = useState('Select gateway')

  const [projectsBnt, setProjectsButton] = useState(null)
  const [gatewaysBtn, setGatewaysBtn] = useState(null)

  const getProjects = async () => {
    try {
      let response = await fetchProjects()
      setProjectsButton(response.data.map((project) => <li><button className="dropdown-item"> {project.name} </button></li>))
    } catch ( err ) {
      console.log(err)
    }
  } 

  const getGateways = async () => {
    try {
      let response = await fetchGateways()
      setGatewaysBtn(response.data.map((gatway) => <li><button className="dropdown-item"> {gatway.name} </button></li>))
    } catch ( err ) {
      console.log(err)
    }
  }

  useEffect(()=> {
    getProjects()
    getGateways()
  }, [])

  const displayAllProjects = () => {
    setProjectName('All projects')
    setGatewayName('All gateways')
    setDisplayNoData(false)
    setDisplayProjects('d-block')
  }

  return (
    <>
        <Page> 
          <div className="d-flex justify-content-between">
            <div className="container">
              <h4 className="p-0 m-0 fs-bolder text-blue-900">
                Reports
              </h4>
              <span className="text-gray p-0 m-0 fw-700">
                Easily generate a report of your transactions
              </span>
            </div>
            <div className="d-flex justify-content-end">
              {/* Projects Dropdown */}
              <div className="dropdown me-4">
                  <button className="btn btn-sm btn-green text-white dropdown-toggle" type="button" id="projects" data-bs-toggle="dropdown" aria-expanded="false">
                    { projectName }
                  </button>
                  <ul className="dropdown-menu" aria-labelledby="projects">
                    <li><button onClick={displayAllProjects} className="dropdown-item"> All projects </button></li>
                      { projectsBnt }
                  </ul>
              </div>

              {/* Gateways Dropdown */}
              <div className="dropdown me-4">
                  <button className="btn btn-sm btn-green text-white dropdown-toggle" type="button" id="gateways" data-bs-toggle="dropdown" aria-expanded="false">
                    { gatewayName }
                  </button>
                  <ul className="dropdown-menu" aria-labelledby="gateways">
                    <li><button onClick={displayAllProjects} className="dropdown-item"> All gateways </button></li>
                      { gatewaysBtn }
                  </ul>
              </div>
          </div>
          </div>

          <NoData 
            display={displayNoData}
          />

          <div className="d-flex justify-content-even">
          <div className={`${displayProjects} rounded-more p-3 bg-lightblue mt-3 mb-3 me-2 ${displayGateways ? 'width-100' : 'width-50'}`} >
            <span className="text-blue-900 fw-700">
              { projectName } | { gatewayName }
            </span>

            <div class="accordion accordion-flush" id="accordionFlushExample">
              <div class="accordion-item">
                <h2 class="accordion-header" id="flush-headingOne">
                  <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#flush-collapseOne" aria-expanded="false" aria-controls="flush-collapseOne">
                    Accordion Item #1
                  </button>
                </h2>
                <div id="flush-collapseOne" class="accordion-collapse collapse" aria-labelledby="flush-headingOne" data-bs-parent="#accordionFlushExample">
                  <div class="accordion-body">Placeholder content for this accordion, which is intended to demonstrate the <code>.accordion-flush</code> class. This is the first item's accordion body.</div>
                </div>
              </div>
              <div class="accordion-item">
                <h2 class="accordion-header" id="flush-headingTwo">
                  <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#flush-collapseTwo" aria-expanded="false" aria-controls="flush-collapseTwo">
                    Accordion Item #2
                  </button>
                </h2>
                <div id="flush-collapseTwo" class="accordion-collapse collapse" aria-labelledby="flush-headingTwo" data-bs-parent="#accordionFlushExample">
                  <div class="accordion-body">Placeholder content for this accordion, which is intended to demonstrate the <code>.accordion-flush</code> class. This is the second item's accordion body. Let's imagine this being filled with some actual content.</div>
                </div>
              </div>
              <div class="accordion-item">
                <h2 class="accordion-header" id="flush-headingThree">
                  <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#flush-collapseThree" aria-expanded="false" aria-controls="flush-collapseThree">
                    Accordion Item #3
                  </button>
                </h2>
                <div id="flush-collapseThree" class="accordion-collapse collapse" aria-labelledby="flush-headingThree" data-bs-parent="#accordionFlushExample">
                  <div class="accordion-body">Placeholder content for this accordion, which is intended to demonstrate the <code>.accordion-flush</code> class. This is the third item's accordion body. Nothing more exciting happening here in terms of content, but just filling up the space to make it look, at least at first glance, a bit more representative of how this would look in a real-world application.</div>
                </div>
              </div>
            </div>



          </div>



          <div className={`${displayGateways} rounded-more p-3 bg-lightblue mt-3 mb-3 ${displayGateways ? 'width-100' : 'width-50'}`} >
            <span className="text-blue-900 fw-700">
              { projectName } | { gatewayName }
            </span>
          </div>

          </div>

        </Page>
    </>
  )
}
