import React, { useEffect, useState } from 'react'
import Dropdown from '../components/Buttons/Dropdown'
import Navbar from '../components/Navbar/Navbar'
import NoData from '../components/Templates/NoData'
import Page from '../components/Templates/Page'
import { fetchProjects } from '../utils/api'

export default function Home() {
  const [displayNoData, setDisplayNoData] = useState(true)
  const [displayProjects, setDisplayProjects] = useState('d-none')
  const [projectsList, setProjectsList] = useState(null)

  const [projectsBnt, setProjectsButton] = useState(null)

  const getProjects = async () => {
    try {
      let response = await fetchProjects()
      setProjectsList(response)
      setDisplayNoData(false)
      setDisplayProjects('d-block')
      setProjectsButton(<Dropdown id="projects" name="Select project" optionsList={projectsList} onClick={ () => {console.log('click 1') }}/>)
    } catch ( err ) {
      console.log(err)
    }
  } 

  useEffect(()=> {
    getProjects()
  }, [])

  return (
    <>
        <Page> 
          <div className="d-flex justify-content-between">
            <div className="container">
              <h4 className="p-0 m-0 fs-bolder text-blue-900">
                Reports
              </h4>
              <span className="text-gray p-0 m-0 fw-700">
                Easily generate a report of your transactions
              </span>
            </div>
            <div className="d-flex justify-content-end">
              { button1 }
              {/* <Dropdown id="gateway" name="Select gateway"/> */}
          </div>
          </div>
          <NoData 
            display={displayNoData}
          />
          <div className={`${displayProjects} container rounded-more p-3 bg-lightblue mt-3 mb-3`} >
            test
          </div>
        </Page>
    </>
  )
}
