export const groupResponse = (projects, gateways, reports) => {
    let response = []
    projects.forEach(project => {
        let singleProject = []
        singleProject[0] = project.name
        reports.forEach(report => {
            for ( let i = 0; i <= report.lenght; i++ ) {
                if ( project.projectId === report.projectId ) {
                    let singleTransaction = []
                    singleTransaction[i] = report.paymentId
                    singleTransaction.details = {
                        created: report.created,
                        modified: report.modified,
                        gatewayId: report.gatewayId,

                    }
                }
            }
        });
        response.push(singleProject)
    });
    console.log(response)
}
