export const searchGateway = (gateways, element) => {
    var result = ''
    gateways.forEach(el => {
        if ( el.gatewayId === element.gatewayId) {
            result = el.name
        }
    });
    return result
}

export const findProjectById = (id, projects) => {
    var result = ''
    if ( id === '' ) {
        result = 'All projects'
    }
    projects.forEach((el) => {
        console.log(el)
        if ( el.projectId === id ) {
            result = el.name
        }
    })
    return result
}
